var page=2;var loadmore=function(){document.addEventListener("click",function(event){if(!event.target.matches(".loadmore"))return;event.preventDefault();var data="action=load_posts_by_ajax&page="+page+"&security="+blog.security;var xhr=new XMLHttpRequest;xhr.open("POST",blog.ajaxurl);xhr.setRequestHeader("Content-Type","application/x-www-form-urlencoded");xhr.onload=function(){if(xhr.status===200&&xhr.response.trim()!==""){var postlist=document.body.querySelector(".posts-list");var posts=document.createRange().createContextualFragment(xhr.responseText);postlist.appendChild(posts);page++}else{var loadmore=document.querySelector(".loadmore");loadmore.hidden=true}};xhr.send(data)},false)};if(document.readyState==="complete"||document.readyState!=="loading"&&!document.documentElement.doScroll){loadmore()}else{document.addEventListener("DOMContentLoaded",loadmore)}